import React, {Component} from 'react';
import './SidebarItem.css';

class SidebarItem extends Component
{
  constructor(props)
  {
    super(props);

    this.itemStyle = {
      height: props.height,
      borderRadius:'20px',
      width:'100%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      border: '1px solid #555'
    }

    this.state = {
      selected: false,
      hovered: false
    }
    //console.log(props);
  }

  onClickHandler = (event) =>
  {

    this.props.click();
  }

  render ()
  {
    return (
      <button className='SidebarItem' style={this.itemStyle} onClick={this.onClickHandler}>
        {this.props.children}
      </button>
    );
  }
}
export default SidebarItem;
