import React, { Component } from 'react';
import './App.css';
import Banner from './Banner/Banner';
import Sidebar from './Sidebar/Sidebar';
import MainContent from './MainContent/MainContent';

class App extends Component {
  constructor(props)
  {
    super(props);
    this.navItems = {
      'home': { msg: 'Welcome home' },
      'about me': { msg: 'about page'},
      'contact': { msg: 'contact me here'},
      'stream': { msg: 'this feature isn\'t ready yet'},
      'misc': {msg: 'I bet you want to know what goes here'}
    };

    this.state = {
      selectedItem: 'home'
    }
  }

  selectItem = (item) => {
    this.setState({
      selectedItem: item
    })
  }

  sidebarClickHandler = (event) => {
    const item = event.target.textContent;

    this.selectItem(item);
  }



  render () {
    return (
      <div className="App">
        <Banner click={() => this.selectItem('home')}/>
        <Sidebar navKeys={Object.keys(this.navItems)} click={this.sidebarClickHandler}/>;
        <MainContent>
          <h1>{this.navItems[this.state.selectedItem].msg}</h1>
        </MainContent>
        <div style={{clear:'both'}}></div>
      </div>
    );
  }
}

export default App;
