import React, { Component } from 'react';
import './App.css';
import Banner from './Banner/Banner';
import Sidebar from './Sidebar/Sidebar';
import MainContent from './MainContent/MainContent';

class App extends Component {
  constructor(props)
  {
    super(props);
    this.navItems = {
      'home': { msg: 'Welcome home' },
      'about me': { msg: 'about page'},
      'contact': { msg: 'contact me here'},
      'stream': { msg: 'this feature isn\'t ready yet'},
      'misc': {msg: 'I bet you want to know what goes here'}
    };

  }


  sidebarClicked = (event) => {
    const item = this.navItems[event.target.textContent];

    console.log(item.msg);
  }

  render () {
    return (
      <div className="App">
        <Banner/>
        <Sidebar navKeys={Object.keys(this.navItems)} click={this.sidebarClicked}/>;
        <MainContent>
          {true? <h1>hello world</h1> :<p>hi mom</p>}
        </MainContent>
        <div style={{clear:'both'}}></div>
      </div>
    );
  }
}

export default App;
